import { addRole, editRole, duplicateCheck } from '@/api/api';
export default {
  name: "RoleModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      roleDisabled: false,
      model: {},
      layout: {
        labelCol: {
          span: 3
        },
        wrapperCol: {
          span: 14
        }
      },
      confirmLoading: false,
      validatorRules: {
        roleName: [{
          required: true,
          message: '请输入角色名称!'
        }, {
          min: 2,
          max: 30,
          message: '长度在 2 到 30 个字符',
          trigger: 'blur'
        }],
        roleCode: [{
          required: true,
          message: '请输入角色名称!'
        }, {
          min: 0,
          max: 64,
          message: '长度不超过 64 个字符',
          trigger: 'blur'
        }, {
          validator: this.validateRoleCode
        }],
        description: [{
          min: 0,
          max: 126,
          message: '长度不超过 126 个字符',
          trigger: 'blur'
        }]
      }
    };
  },
  created: function created() {
    //备份model原始值
    this.modelDefault = JSON.parse(JSON.stringify(this.model));
  },
  methods: {
    add: function add() {
      this.edit(this.modelDefault);
    },
    edit: function edit(record) {
      this.model = Object.assign({}, record);
      this.visible = true; //编辑页面禁止修改角色编码

      if (this.model.id) {
        this.roleDisabled = true;
      } else {
        this.roleDisabled = false;
      }
    },
    close: function close() {
      this.$refs.form.clearValidate();
      this.$emit('close');
      this.visible = false;
    },
    handleOk: function handleOk() {
      var _this = this;

      var that = this; // 触发表单验证

      this.$refs.form.validate(function (valid) {
        if (valid) {
          that.confirmLoading = true;
          var obj;

          if (!_this.model.id) {
            obj = addRole(_this.model);
          } else {
            obj = editRole(_this.model);
          }

          obj.then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        } else {
          return false;
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    validateRoleCode: function validateRoleCode(rule, value, callback) {
      if (/[\u4E00-\u9FA5]/g.test(value)) {
        callback("角色编码不可输入汉字!");
      } else {
        var params = {
          tableName: "sys_role",
          fieldName: "role_code",
          fieldVal: value,
          dataId: this.model.id
        };
        duplicateCheck(params).then(function (res) {
          if (res.success) {
            callback();
          } else {
            callback(res.message);
          }
        });
      }
    }
  }
};
