import pick from 'lodash.pick';
import { addDictItem, editDictItem } from '@/api/api';
import { getAction } from '@api/manage';
export default {
  name: "DictItemModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      visibleCheck: true,
      model: {},
      dictId: "",
      status: 1,
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      validatorRules: {
        itemText: [{
          required: true,
          message: '请输入名称!'
        }],
        itemValue: [{
          required: true,
          message: '请输入数据值!'
        }, {
          validator: this.validateItemValue
        }]
      }
    };
  },
  created: function created() {},
  methods: {
    add: function add(dictId) {
      this.dictId = dictId; //初始化默认值

      this.edit({
        sortOrder: 1,
        status: 1
      });
    },
    edit: function edit(record) {
      if (record.id) {
        this.dictId = record.dictId;
      }

      this.status = record.status;
      this.visibleCheck = record.status == 1 ? true : false;
      this.model = Object.assign({}, record);
      this.model.dictId = this.dictId;
      this.model.status = this.status;
      this.visible = true;
    },
    onChose: function onChose(checked) {
      if (checked) {
        this.status = 1;
        this.visibleCheck = true;
      } else {
        this.status = 0;
        this.visibleCheck = false;
      }
    },
    // 确定
    handleOk: function handleOk() {
      var _this = this;

      var that = this; // 触发表单验证

      this.$refs.form.validate(function (valid) {
        if (valid) {
          that.confirmLoading = true;
          _this.model.itemText = (_this.model.itemText || '').trim();
          _this.model.itemValue = (_this.model.itemValue || '').trim();
          _this.model.description = (_this.model.description || '').trim();
          _this.model.status = _this.status;
          var obj;

          if (!_this.model.id) {
            obj = addDictItem(_this.model);
          } else {
            obj = editDictItem(_this.model);
          }

          obj.then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        } else {
          return false;
        }
      });
    },
    // 关闭
    handleCancel: function handleCancel() {
      this.close();
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
      this.$refs.form.resetFields();
    },
    validateItemValue: function validateItemValue(rule, value, callback) {
      var param = {
        itemValue: value,
        dictId: this.dictId
      };

      if (this.model.id) {
        param.id = this.model.id;
      }

      if (value) {
        var reg = new RegExp("[`~!@#$^&*()=|{}'.<>《》/?！￥（）—【】‘；：”“。，、？]");

        if (reg.test(value)) {
          callback("数据值不能包含特殊字符！");
        } else {
          //update--begin--autor:lvdandan-----date:20201203------for：JT-27【数据字典】字典 - 数据值可重复
          getAction("/sys/dictItem/dictItemCheck", param).then(function (res) {
            if (res.success) {
              callback();
            } else {
              callback(res.message);
            }
          }); //update--end--autor:lvdandan-----date:20201203------for：JT-27【数据字典】字典 - 数据值可重复
        }
      } else {
        callback();
      }
    }
  }
};
