import { getAction } from '@api/manage';
export default {
  name: 'JSelectMultiple',
  props: {
    placeholder: {
      type: String,
      default: '',
      required: false
    },
    value: {
      type: String,
      required: false
    },
    readOnly: {
      type: Boolean,
      required: false,
      default: false
    },
    options: {
      type: Array,
      default: function _default() {
        return [];
      },
      required: false
    },
    triggerChange: {
      type: Boolean,
      required: false,
      default: false
    },
    spliter: {
      type: String,
      required: false,
      default: ','
    },
    popContainer: {
      type: String,
      default: '',
      required: false
    },
    dictCode: {
      type: String,
      required: false
    }
  },
  data: function data() {
    return {
      arrayValue: !this.value ? [] : this.value.split(this.spliter),
      dictOptions: []
    };
  },
  computed: {
    selectOptions: function selectOptions() {
      return this.dictOptions.length > 0 ? this.dictOptions : this.options;
    }
  },
  watch: {
    value: function value(val) {
      if (!val) {
        this.arrayValue = [];
      } else {
        this.arrayValue = this.value.split(this.spliter);
      }
    }
  },
  mounted: function mounted() {
    if (this.dictCode) {
      this.loadDictOptions();
    }
  },
  methods: {
    onChange: function onChange(selectedValue) {
      if (this.triggerChange) {
        this.$emit('change', selectedValue.join(this.spliter));
      } else {
        this.$emit('input', selectedValue.join(this.spliter));
      }
    },
    getParentContainer: function getParentContainer(node) {
      if (!this.popContainer) {
        return node.parentNode;
      } else {
        return document.querySelector(this.popContainer);
      }
    },
    // 根据字典code查询字典项
    loadDictOptions: function loadDictOptions() {
      var _this = this;

      getAction("/sys/dict/getDictItems/".concat(this.dictCode), {}).then(function (res) {
        if (res.success) {
          _this.dictOptions = res.result.map(function (item) {
            return {
              value: item.value,
              label: item.text
            };
          });
        } else {
          console.error('getDictItems error: : ', res);
          _this.dictOptions = [];
        }
      });
    }
  }
};
