import { httpAction } from '@/api/manage';
import pick from 'lodash.pick';
import { getAction } from '@/api/manage';
import JDate from '@/components/jeecg/JDate';
import JEditor from '@/components/jeecg/JEditor';
import SelectUserListModal from "./SelectUserListModal";
import moment from 'moment';
export default {
  components: {
    JEditor: JEditor,
    JDate: JDate,
    SelectUserListModal: SelectUserListModal
  },
  name: "SysAnnouncementModal",
  data: function data() {
    return {
      title: "操作",
      visible: false,
      disableSubmit: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 4
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 18
        }
      },
      labelColX1: {
        xs: {
          span: 24
        },
        sm: {
          span: 4
        }
      },
      wrapperColX1: {
        xs: {
          span: 24
        },
        sm: {
          span: 18
        }
      },
      confirmLoading: false,
      validatorRules: {
        titile: [{
          required: true,
          message: '请输入标题!'
        }],
        msgCategory: [{
          required: true,
          message: '请选择消息类型!'
        }],
        msgType: [{
          required: true,
          message: '请选择通告对象类型!'
        }],
        endTime: [{
          required: true,
          message: '请选择结束时间!'
        }, {
          validator: this.endTimeValidate
        }],
        startTime: [{
          required: true,
          message: '请选择开始时间!'
        }, {
          validator: this.startTimeValidate
        }],
        msgAbstract: [{
          required: true,
          message: '请输入摘要!'
        }]
      },
      url: {
        queryByIds: "/sys/user/queryByIds",
        add: "/sys/annountCement/add",
        edit: "/sys/annountCement/edit"
      },
      userType: false,
      userIds: "",
      selectedUser: [],
      disabled: false,
      msgContent: "",
      userList: [],
      returnKeys: ['id', 'id'] //用户选择返回字段

    };
  },
  created: function created() {},
  methods: {
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      this.model = {};
      this.disable = false;
      this.visible = true;
      this.getUser(record);
    },
    getUser: function getUser(record) {
      //update-begin---author:wangshuai ---date:20211227  for：[JTC-191]系统通告参考vue3的来改，为单选按钮附默认值------------
      record.msgCategory = record.msgCategory ? record.msgCategory : "1";
      record.msgType = record.msgType ? record.msgType : "ALL";
      record.priority = record.priority ? record.priority : "H"; //update-begin---author:wangshuai ---date:20211227  for：[JTC-191]系统通告参考vue3的来改，为单选按钮附默认值------------

      this.model = Object.assign({}, record); // 指定用户

      if (record && record.msgType === "USER") {
        this.userType = true; //update-begin---author:wangshuai ---date:20220104  for：[JTC-304]指定人员不支持分页勾选,换通用的用户组件------------

        this.userIds = record.userIds.substr(0, record.userIds.length - 1); //update-end---author:wangshuai ---date:20220104  for：[JTC-304]指定人员不支持分页勾选,换通用的用户组件------------
      }
    },
    close: function close() {
      this.$emit('close');
      this.selectedUser = [];
      this.visible = false;
      this.$refs.form.resetFields();
    },
    handleOk: function handleOk() {
      var _this = this;

      var that = this; //当设置指定用户类型，但用户为空时，后台报错

      if (this.userType && !(this.userIds != null && this.userIds.length > 0)) {
        this.$message.warning('指定用户不能为空！');
        return;
      } // 触发表单验证


      this.$refs.form.validate(function (valid) {
        if (valid) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this.model.id) {
            httpurl += _this.url.add;
            method = 'post';
          } else {
            httpurl += _this.url.edit;
            method = 'put';
          }

          if (_this.userType) {
            //update-begin---author:wangshuai ---date:20220104  for：[JTC-304]指定人员不支持分页勾选,换通用的用户组件------------
            _this.model.userIds = _this.userIds + ","; //update-end---author:wangshuai ---date:20220104  for：[JTC-304]指定人员不支持分页勾选,换通用的用户组件------------
          }

          httpAction(httpurl, _this.model, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
              that.resetUser();
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        } else {
          return false;
        }
      });
    },
    handleCancel: function handleCancel() {
      this.visible = false;
      this.$emit('close');
      this.$refs.form.resetFields();
      this.resetUser();
    },
    resetUser: function resetUser() {
      this.userType = false; //update-begin---author:wangshuai ---date:20220318  for：[issues/I4X63V]vue有些页面报错，但是在线演示的却没有-----

      this.userIds = ""; //update-end---author:wangshuai ---date:20220318  for：[issues/I4X63V]vue有些页面报错，但是在线演示的却没有-----

      this.disabled = false;
    },
    chooseMsgType: function chooseMsgType(e) {
      if ("USER" == e.target.value) {
        this.userType = true;
      } else {
        this.userType = false; //update-begin---author:wangshuai ---date:20220318  for：[issues/I4X63V]vue有些页面报错，但是在线演示的却没有-----

        this.userIds = ""; //update-end---author:wangshuai ---date:20220318  for：[issues/I4X63V]vue有些页面报错，但是在线演示的却没有-----
      }
    },
    startTimeValidate: function startTimeValidate(rule, value, callback) {
      var endTime = this.model.endTime;

      if (!value || !endTime) {
        callback();
      } else if (moment(value).isBefore(endTime)) {
        callback();
      } else {
        callback("开始时间需小于结束时间");
      }
    },
    endTimeValidate: function endTimeValidate(rule, value, callback) {
      var startTime = this.model.startTime;

      if (!value || !startTime) {
        callback();
      } else if (moment(startTime).isBefore(value)) {
        callback();
      } else {
        callback("结束时间需大于开始时间");
      }
    }
  }
};
