import { httpAction } from '@/api/manage';
export default {
  name: 'PermissionDataRuleModal',
  data: function data() {
    return {
      queryParam: {},
      title: '操作',
      visible: false,
      model: {},
      ruleConditionList: [],
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      permissionId: '',
      validatorRules: {
        ruleConditions: [{
          required: true,
          message: '请选择条件!'
        }],
        ruleName: [{
          required: true,
          message: '请输入规则名称!'
        }],
        ruleValue: [{
          required: true,
          message: '请输入规则值!'
        }],
        ruleColumn: []
      },
      url: {
        list: '/sys/dictItem/list',
        add: '/sys/permission/addPermissionRule',
        edit: '/sys/permission/editPermissionRule'
      },
      showRuleColumn: true
    };
  },
  created: function created() {},
  methods: {
    add: function add(permId) {
      this.permissionId = permId; //初始化默认值

      this.edit({
        status: '1'
      });
    },
    edit: function edit(record) {
      this.model = Object.assign({}, record);

      if (record.permissionId) {
        this.model.permissionId = record.permissionId;
      } else {
        this.model.permissionId = this.permissionId;
      }

      this.visible = true;
      this.initRuleCondition();
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
      this.$refs.form.resetFields();
    },
    handleOk: function handleOk() {
      var _this = this;

      var that = this; // 触发表单验证

      this.$refs.form.validate(function (valid) {
        if (valid) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this.model.id) {
            httpurl += _this.url.add;
            method = 'post';
          } else {
            httpurl += _this.url.edit;
            method = 'put';
          }

          httpAction(httpurl, _this.model, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        } else {
          return false;
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    },
    initRuleCondition: function initRuleCondition() {
      if (this.model.ruleConditions && this.model.ruleConditions == 'USE_SQL_RULES') {
        this.showRuleColumn = false;
      } else {
        this.showRuleColumn = true;
      }
    },
    handleChangeRuleCondition: function handleChangeRuleCondition(val) {
      if (val == 'USE_SQL_RULES') {
        this.model.ruleColumn = '';
        this.showRuleColumn = false;
      } else {
        this.showRuleColumn = true;
      }
    }
  }
};
