import { httpAction } from '@/api/manage';
import JDate from '@/components/jeecg/JDate';
import pick from 'lodash.pick';
import moment from "moment";
export default {
  name: "JeecgOrderDMainModal",
  components: {
    JDate: JDate
  },
  data: function data() {
    return {
      title: "操作",
      visible: false,
      orderMainModel: {
        jeecgOrderCustomerList: [{}],
        jeecgOrderTicketList: [{}]
      },
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      validatorRules: {
        orderCode: [{
          required: true,
          message: '请输入订单号!'
        }]
      },
      url: {
        add: "/test/order/add",
        edit: "/test/order/edit",
        orderCustomerList: "/test/order/listOrderCustomerByMainId",
        orderTicketList: "/test/order/listOrderTicketByMainId"
      }
    };
  },
  methods: {
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      this.orderMainModel = Object.assign({}, record);
      console.log(this.orderMainModel); //初始化明细表数据

      this.visible = true;
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
      this.$refs.form.resetFields();
    },
    handleOk: function handleOk() {
      var _this = this;

      var that = this; // 触发表单验证

      this.$refs.form.validate(function (valid) {
        if (valid) {
          that.confirmLoading = true;
          var httpurl = '';
          var method = '';

          if (!_this.orderMainModel.id) {
            httpurl += _this.url.add;
            method = 'post';
          } else {
            httpurl += _this.url.edit;
            method = 'put';
          }

          httpAction(httpurl, _this.orderMainModel, method).then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        } else {
          return false;
        }
      });
    },
    handleCancel: function handleCancel() {
      this.close();
    }
  }
};
