import pick from 'lodash.pick';
import { addDict, editDict, duplicateCheck } from '@/api/api';
export default {
  name: 'DictModal',
  data: function data() {
    return {
      value: 1,
      title: '操作',
      visible: false,
      model: {},
      labelCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 5
        }
      },
      wrapperCol: {
        xs: {
          span: 24
        },
        sm: {
          span: 16
        }
      },
      confirmLoading: false,
      validatorRules: {
        dictName: [{
          required: true,
          message: '请输入字典名称!'
        }],
        dictCode: [{
          required: true,
          message: '请输入字典编码!'
        }, {
          validator: this.validateDictCode
        }]
      }
    };
  },
  created: function created() {},
  methods: {
    validateDictCode: function validateDictCode(rule, value, callback) {
      // 重复校验
      var params = {
        tableName: 'sys_dict',
        fieldName: 'dict_code',
        fieldVal: value,
        dataId: this.model.id
      };
      duplicateCheck(params).then(function (res) {
        if (res.success) {
          callback();
        } else {
          callback(res.message);
        }
      });
    },
    handleChange: function handleChange(value) {
      this.model.status = value;
    },
    add: function add() {
      this.edit({});
    },
    edit: function edit(record) {
      if (record.id) {
        this.visiblekey = true;
      } else {
        this.visiblekey = false;
      }

      this.model = Object.assign({}, record);
      this.visible = true;
    },
    // 确定
    handleOk: function handleOk() {
      var _this = this;

      var that = this; // 触发表单验证

      this.$refs.form.validate(function (valid) {
        if (valid) {
          that.confirmLoading = true;
          _this.model.dictName = (_this.model.dictName || '').trim();
          _this.model.dictCode = (_this.model.dictCode || '').trim();
          _this.model.description = (_this.model.description || '').trim();
          var obj;

          if (!_this.model.id) {
            obj = addDict(_this.model);
          } else {
            obj = editDict(_this.model);
          }

          obj.then(function (res) {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(function () {
            that.confirmLoading = false;
            that.close();
          });
        } else {
          return false;
        }
      });
    },
    // 关闭
    handleCancel: function handleCancel() {
      this.close();
    },
    close: function close() {
      this.$emit('close');
      this.visible = false;
      this.$refs.form.resetFields();
    }
  }
};
