import {httpAction} from '@/api/manage'
import JDate from '@/components/jeecg/JDate'
import pick from 'lodash.pick'
import moment from "moment"

export default {
  name: "JeecgOrderDMainModal",
  components: {
    JDate
  },
  data() {
    return {
      title: "操作",
      visible: false,
      orderMainModel: {
        jeecgOrderCustomerList: [{}],
        jeecgOrderTicketList: [{}]
      },
      labelCol: {
        xs: {span: 24},
        sm: {span: 5},
      },
      wrapperCol: {
        xs: {span: 24},
        sm: {span: 16},
      },
      confirmLoading: false,
      validatorRules: {
        orderCode: [
          { required: true, message: '请输入订单号!' }
        ]
      },
      url: {
        add: "/test/order/add",
        edit: "/test/order/edit",
        orderCustomerList: "/test/order/listOrderCustomerByMainId",
        orderTicketList: "/test/order/listOrderTicketByMainId",
      },
    }
  },
  methods: {
    add() {
      this.edit({});
    },
    edit(record) {
      this.orderMainModel = Object.assign({}, record);
      console.log(this.orderMainModel)
      //初始化明细表数据
      this.visible = true;
    },
    close() {
      this.$emit('close');
      this.visible = false;
      this.$refs.form.resetFields();
    },
    handleOk() {
      const that = this;
      // 触发表单验证
      this.$refs.form.validate(valid => {
        if (valid) {
          that.confirmLoading = true;
          let httpurl = '';
          let method = '';
          if (!this.orderMainModel.id) {
            httpurl += this.url.add;
            method = 'post';
          } else {
            httpurl += this.url.edit;
            method = 'put';
          }

          httpAction(httpurl, this.orderMainModel, method).then((res) => {
            if (res.success) {
              that.$message.success(res.message);
              that.$emit('ok');
            } else {
              that.$message.warning(res.message);
            }
          }).finally(() => {
            that.confirmLoading = false;
            that.close();
          })
        }else{
          return false;
        }
      })
    },
    handleCancel() {
      this.close()
    }
  }
}
