import CompetitionForm from './CompetitionForm'

export default {
  name: 'CompetitionModal',
  components: {
    CompetitionForm
  },
  data() {
    return {
      title:'',
      width:800,
      visible: false,
      disableSubmit: false
    }
  },
  methods:{
    add () {
      this.visible=true
      this.$nextTick(()=>{
        this.$refs.realForm.add();
      })
    },
    edit (record) {
      this.visible=true
      this.$nextTick(()=>{
        this.$refs.realForm.edit(record);
      })
    },
    close () {
      this.$emit('close');
      this.visible = false;
    },
    handleOk () {
      this.$refs.realForm.handleOk();
    },
    submitCallback(){
      this.$emit('ok');
      this.visible = false;
    },
    handleCancel () {
      this.close()
    }
  }
}
